// Challenge / Exercise
import { createBrowserRouter } from "react-router-dom";
import { RouterProvider } from "react-router-dom";
import RootLayout from "./Pages/Root";
import HomePage from "./Pages/Home";
import EventRootLayout from "./Pages/EventRoot";
import EventDetail, { loader as detailLoader ,action as deleteAction} from "./Pages/EventDetail";
import NewEvent,{action as newEventAction} from "./Pages/NewEvent";
import EditEvent from "./Pages/EditEvent";
import EventsPage, { loader as eventsLoader } from "./Pages/Events";
import ErrorPage from "./Pages/Error";

const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout></RootLayout>,
    errorElement: <ErrorPage></ErrorPage>,
    children: [
      { index: true, element: <HomePage></HomePage> },
      {
        path: "events",
        element: <EventRootLayout></EventRootLayout>,
        children: [
          {
            index: true,
            element: <EventsPage></EventsPage>,
            loader: eventsLoader,
          },
          {
            path:':eventId',
            id:'event-detail',
            loader:detailLoader,
            children:[
              {
                index:true,
                element: <EventDetail></EventDetail>,
                action:deleteAction
              },
              { path: "edit", element: <EditEvent></EditEvent> },

            ]
          },
          { path: "new", element: <NewEvent></NewEvent>,action:newEventAction },

        ],
      },
    ],
  },
]);

function App() {
  return <RouterProvider router={router}></RouterProvider>;
}

export default App;
